-- 首页信息
drop table if exists `homeinfo`;
CREATE TABLE homeinfo (
    id INT AUTO_INCREMENT PRIMARY KEY,
    hometype  VARCHAR(20) NOT NULL,
    img text not null,
    title varchar(200) not null,
    likenum int not null,
    avater longblob not null,
    avatertext varchar(20) not null,
    writedate datetime not null,
    descinfo varchar(10000) not null
);
